// SearchBar.js
import React, { useState } from 'react';
import { Search, X } from 'lucide-react';

const SearchBar = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [isFocused, setIsFocused] = useState(false);

  const handleClear = () => {
    setSearchTerm('');
  };

  return (
    <div className="relative max-w-xl w-full">
      <div className={`
        flex items-center bg-white bg-opacity-10 rounded-full
        transition-all duration-300
        ${isFocused ? 'bg-opacity-20 ring-2 ring-green-500' : 'hover:bg-opacity-15'}
      `}>
        <Search 
          className="h-5 w-5 text-gray-300 ml-4" 
        />
        <input
          type="text"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          onFocus={() => setIsFocused(true)}
          onBlur={() => setIsFocused(false)}
          placeholder="검색"
          className="w-full px-4 py-2 bg-transparent text-white placeholder-gray-300 
                     focus:outline-none"
        />
        {searchTerm && (
          <button
            onClick={handleClear}
            className="p-2 hover:text-green-500 transition-colors mr-2"
          >
            <X className="h-5 w-5 text-gray-300 hover:text-green-500" />
          </button>
        )}
      </div>
      
      {/* 검색 결과 드롭다운 */}
      {searchTerm && (
        <div className="absolute mt-2 w-full bg-white rounded-lg shadow-lg 
                        border border-gray-200 max-h-96 overflow-y-auto z-50">
          <div className="p-4 border-b">
            <p className="text-sm text-gray-500">
              '{searchTerm}'에 대한 검색 결과
            </p>
          </div>
          {/* 여기에 실제 검색 결과를 매핑하면 됩니다 */}
          <div className="p-2">
            <div className="p-2 hover:bg-gray-100 rounded cursor-pointer">
              <p className="font-medium">난 아무것도 모른다</p>
              <p className="text-sm text-gray-500">{searchTerm}에 대해</p>
            </div>
            <div className="p-2 hover:bg-gray-100 rounded cursor-pointer">
              <p className="font-medium">GPT 딸깍</p>
              <p className="text-sm text-gray-500">{searchTerm}라고.</p>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default SearchBar;
