import React, { useState } from 'react';  // useState import 추가
import { Menu } from 'lucide-react';
import Sidebar from './Sidebar';
import LoginForm from './LoginForm';

const Navbar = () => {
  // useState 선언 추가
  const [isLoginOpen, setIsLoginOpen] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);  // 사이드바 상태 추가

  const handleLoginSuccess = () => {
    console.log('로그인 성공!');
    setIsLoggedIn(true);
    setIsLoginOpen(false);
  };

  // 사이드바 열기/닫기 핸들러 추가
  const handleOpenSidebar = () => {
    setIsSidebarOpen(true);
  };

  const handleCloseSidebar = () => {
    setIsSidebarOpen(false);
  };

  return (
    <div className="h-screen overflow-hidden">
      <nav className="bg-black shadow-lg p-4 flex justify-between items-center fixed top-0 left-0 right-0 z-20">
        <div className="flex items-center">
          <button
            onClick={handleOpenSidebar}
            className="p-2 hover:bg-gray-800 rounded-full mr-4"
          >
            <Menu size={24} color="white" />
          </button>
          <div className="text-2xl font-bold text-white">GameSnap</div>
        </div>
        
        <div className="flex items-center">
          {!isLoggedIn && (
            <button
              onClick={() => setIsLoginOpen(true)}
              className="px-6 py-3 bg-white text-black-600 font-semibold rounded-md hover:bg-green-700 hover:text-white"
            >
              회원 로그인
            </button>
          )}
          {isLoggedIn && (
            <div className="text-white px-6 py-3">
              환영합니다!
            </div>
          )}
        </div>
      </nav>

      {/* 사이드바 */}
      <Sidebar
        isOpen={isSidebarOpen} 
        onClose={handleCloseSidebar}
        className="p-2 hover:bg-gray-800 rounded-full"

      />

      {/* 메인 컨텐츠 */}
      <div className="relative h-full mt-[73px]">
        <div
          className={`absolute inset-0 transition-transform duration-300 ease-in-out ${
            isLoggedIn ? '-translate-x-full' : 'translate-x-0'
          }`}
        >
          <div className="min-h-screen bg-gray-100 p-4">
            <div className="max-w-4xl mx-auto">
              <h1 className="text-3xl font-bold mb-6">메인 페이지</h1>
              <p className="text-gray-600">메인 페이지 내용입니다...</p>
            </div>
          </div>
        </div>
        
        <div
          className={`absolute inset-0 transition-transform duration-300 ease-in-out ${
            isLoggedIn ? 'translate-x-0' : 'translate-x-full'
          }`}
        >
          <div className="min-h-screen bg-white p-4">
            <div className="max-w-4xl mx-auto">
              <h1 className="text-3xl font-bold mb-6">사용자 페이지</h1>
              <p className="text-gray-600">로그인 후 보이는 페이지입니다...</p>
            </div>
          </div>
        </div>
      </div>

      {/* 로그인 모달 */}
      {isLoginOpen && (
        <LoginForm 
          onClose={() => setIsLoginOpen(false)}
          onLoginSuccess={handleLoginSuccess}
        />
      )}
    </div>
  );
};

export default Navbar;
